import { Component, ElementRef, OnChanges, OnInit, SimpleChanges, ViewChild } from '@angular/core';
import { MeetingsService } from '../../services/meetings.service';
import { NewMeetDetail } from 'src/app/shared/modals/newMeetDetail';
import { ActivatedRoute, Router } from '@angular/router';
import { UserDetail } from 'src/app/shared/modals/userDetail';
import { SeatDetail } from 'src/app/shared/modals/seatDetail';

@Component({
  selector: 'app-meetings',
  templateUrl: './meetings.component.html',
  styleUrls: ['./meetings.component.scss']
})
export class MeetingsComponent implements OnInit, OnChanges {
  @ViewChild('videoElement', { static: false }) videoElement!: ElementRef;
  stream!: MediaStream;

  constructor(
    private _meetingServices: MeetingsService,
    private _activeRoute: ActivatedRoute,
    private _router: Router
  ) { }

  ngOnInit(): void {
    this._activeRoute.queryParams.subscribe(
      (res: any) => {
        if (res['meetId']) {
          this.loadMeetDetailsByMeetingCode(res['meetId']);
        }
      }
    )
  }

  ngOnChanges(changes: SimpleChanges): void {
    // if (this._userList) {
    //   this.userList = this._userList;
    //   this.updateUserList()
    // }
    // if (this._requestUserList) {
    //   this.requestUserList = this._requestUserList
    //   this.updateUserList()
    // }
  }

  meetingDetails: NewMeetDetail = new NewMeetDetail();

  loadMeetDetailsByMeetingCode(_meetId: string) {
    this.meetingDetails = this._meetingServices.getMeetDetailsByMeetCode(_meetId);
    if (this.meetingDetails.MeetCode) {
      this.loadUserList();
      this.generateSeatListByMeetSeatQuantity();
    }
  }


  seatList: SeatDetail[] = []
  generateSeatListByMeetSeatQuantity() {
    if (this.meetingDetails.MeetCandidateQuantity) {
      for (let i = 0; i < Number(this.meetingDetails.MeetCandidateQuantity); i++) {
        let _seat = new SeatDetail();
        this.seatList.push(_seat);
      }
    }
  }

  userList: UserDetail[] = [];
  _userList: UserDetail[] = [];
  requestUserList: UserDetail[] = [];
  _requestUserList: UserDetail[] = [];
  loadUserList() {
    let _localStoArray = localStorage;
    if (_localStoArray.length) {
      let __userList: UserDetail[] = [];
      let __requestUserList: UserDetail[] = [];
      // debugger
      for (let i = 0; i < localStorage.length; i++) {
        let _key = localStorage.key(i);
        if (_key?.toLowerCase().includes('user')) {
          let _userDetails: any = localStorage.getItem(_key);
          // console.log(_userDetails);
          if (_userDetails) {
            _userDetails = JSON.parse(_userDetails);
            if (_userDetails.MeetCode === this.meetingDetails.MeetCode) {
              if (_key?.includes('_NewRequest')) {
                __requestUserList.push(_userDetails)
              } else {
                __userList.push(_userDetails);
              }
            }
          }
        }
      }
      if (this._userList.length != __userList.length) {
        this._userList = __userList;
        this.updateUserList();
      }
      if (this._requestUserList.length != __requestUserList.length) {
        this._requestUserList = __requestUserList;
        this.updateUserList()
      }
      // this._requestUserList = __requestUserList
    }
    setInterval(() => {
      this.loadUserList();
    }, 1000);
  }

  showRequestList: boolean = false;
  updateUserList() {
    this.userList = this._userList;
    this.requestUserList = this._requestUserList
  }

  leaveMeeting() {
    // localStorage.removeItem('user');
    this._router.navigate(['dashboard']);
  }

  startCamera() {
    navigator.mediaDevices
      .getUserMedia({ video: true })
      .then((stream) => {
        this.stream = stream;
        this.videoElement.nativeElement.srcObject = stream;
      })
      .catch((error) => {
        console.error('Error accessing camera:', error);
      });
  }

  stopCamera() {
    if (this.stream) {
      this.stream.getTracks().forEach(track => track.stop());
    }
  }

  private mediaStream: MediaStream | null = null;

  async startScreenSharing() {
    try {
      this.mediaStream = await navigator.mediaDevices.getDisplayMedia({
        video: {
          displaySurface: 'browser',
        } as MediaTrackConstraints,
        audio: false
      });

      const videoElement = document.getElementById('screenVideo') as HTMLVideoElement;
      if (videoElement) {
        videoElement.srcObject = this.mediaStream;
      }

      this.mediaStream.getVideoTracks()[0].onended = () => {
        this.stopScreenSharing();
      };
    } catch (error) {
      console.error('Error capturing screen:', error);
    }
  }

  stopScreenSharing() {
    if (this.mediaStream) {
      this.mediaStream.getTracks().forEach(track => track.stop());
      this.mediaStream = null;
    }
  }

  approveMemberRequest(_user: UserDetail) {
    debugger
    let _key = 'user_' + _user.ConatctNo + '_' + _user.MeetCode + '_' + 'NewRequest';
    let _userDetail = localStorage.getItem(_key) || null;
    if (_userDetail) {
      localStorage.removeItem(_key);
      _key = 'user_' + _user.ConatctNo + '_' + _user.MeetCode
      localStorage.setItem(_key, _userDetail);
      this.loadUserList();
    }
  }

  // approveMemberRequest(_user: UserDetail) {
  //   debugger
  //   let _key = 'user_' + _user.ConatctNo + '_' + _user.MeetCode + '_' + 'NewRequest';
  //   let _userDetail = localStorage.getItem(_key) || null;
  //   if (_userDetail) {
  //     localStorage.removeItem(_key);
  //     _key = 'user_' + _user.ConatctNo + '_' + _user.MeetCode
  //     localStorage.setItem(_key, _userDetail);
  //     this.loadUserList();
  //   }
  // }

  isMicOn: boolean = false;
  isVideoOn: boolean = false;
  isScreenShredOn: boolean = false;


}
<ng-container *ngIf="true">
  <img src="assets/images/meeting/office_meet.jpg"
    *ngIf="meetingDetails.MeetName.toLowerCase().includes('office')"
    style="height: 100dvh;" width="100%" alt srcset>
  <img src="assets/images/meeting/class_meet.jpg"
    *ngIf="meetingDetails.MeetName.toLowerCase().includes('class')"
    style="height: 100dvh;" width="100%" alt srcset>
  <img src="assets/images/meeting/cabin_meet.jpg"
    *ngIf="meetingDetails.MeetName.toLowerCase().includes('cabin')"
    style="height: 100dvh;" width="100%" alt srcset>
  <div
    style="position: absolute; bottom: 10%; left: 2%; min-height: 150px !important; max-height: 150px !important; z-index: 10;">
    <video *ngIf="isVideoOn"
      style="min-height: 20dvh !important; max-height: 20dvh !important; width: 20dvw;"
      #videoElement autoplay></video>
  </div>

  <div
    style="position: absolute; bottom: 2%; right: 0%;
    z-index: 20; ">
    <div class="card border-0 shadow-lg h-100" *ngIf="showRequestList"
      style="border-top-right-radius: 0px;
          border-bottom-right-radius: 0px;
          position: absolute;
          right: 0%; overflow: auto;
          bottom: 100%;
          min-width: 500px;
          max-width: 500px;
          min-height: 50dvh;
          max-height: 50dvh;">
      <div id="request_header" class="d-flex justify-content-between w-100 p-2">
        <strong style="font-size: 1.5rem;">
          Request
        </strong>
        <img src="./../../../../assets/images/meeting_menubar_icons/close.png"
          (click)="showRequestList = false"
          style="height: 50px; width: 50px; cursor: pointer;" alt srcset>
      </div>
      <ng-container *ngFor="let user of requestUserList; index as i">
        <div class="d-flex flex-row justify-content-between gap-1"
          style="padding-left: 10px; padding-right: 10px;">
          <div class="d-flex flex-column p-2">
            <strong>{{user.FirstName | uppercase}} {{user.LastName | uppercase}}
            </strong>
            <small>
              <span style="color: red;">Request Date/Time :</span>
              <strong>{{user.MeetJoiningDate | date :
                'dd/MM/yyyy hh:mm a'}}</strong>
            </small>
          </div>
          <div class="d-flex flex-row gap-1">
            <button type="button" class="btn btn-success"
              (click)="approveMemberRequest(user)">
              <img
                src="./../../../../assets/images/meeting_menubar_icons/person_check.png"
                style="height: 30px;" alt srcset>
            </button>
            <!-- <button type="button" class="btn bg-danger">
              <img
                src="./../../../../assets/images/meeting_menubar_icons/person_cancel.png"
                style="height: 30px;" alt srcset>
            </button> -->
          </div>
        </div>
      </ng-container>
    </div>
    <button type="button"
      class="btn hover-effect-btn btn-primary"
      style="border-top-right-radius: 0px;
    border-bottom-right-radius: 0px;" (click)="showRequestList = true;">
      <img
        src="./../../../../assets/images/meeting_menubar_icons/person_request.png"
        style="height: 25px; width: 25px;" alt srcset>
      <strong [class]="showRequestList ? '': 'hover-effect-label'">
        Requests
      </strong>
    </button>
  </div>

  <div style="position: absolute; right: 0; top: 5%; height: 90%;">
    <div class="d-flex justify-items-center align-items-center h-100">
      <div class="card p-2 flex flex-column gap-3"
        style="border-top-right-radius: 0px !important; border-bottom-right-radius: 0px !important;">
        <img class="bg-primary rounded-2 p-1 cursor-pointer"
          *ngIf="isMicOn" (click)="isMicOn = false"
          src="assets/images/meeting_menubar_icons/mic_on.png" height="50px"
          width="50px" alt srcset>
        <img class="bg-secondary rounded-2 p-1 cursor-pointer"
          *ngIf="!isMicOn" (click)="isMicOn = true"
          src="assets/images/meeting_menubar_icons/mic_off.png" height="50px"
          width="50px" alt srcset>
        <img class="bg-primary rounded-2 p-1 cursor-pointer"
          *ngIf="isVideoOn" (click)="isVideoOn = false; stopCamera();"
          src="assets/images/meeting_menubar_icons/video_cam_on.png"
          height="50px" width="50px" alt srcset>
        <img class="bg-secondary rounded-2 p-1 cursor-pointer"
          *ngIf="!isVideoOn" (click)="isVideoOn = true; startCamera();"
          src="assets/images/meeting_menubar_icons/video_cam_off.png"
          height="50px" width="50px" alt srcset>
        <img class="bg-primary rounded-2 p-1 cursor-pointer"
          *ngIf="isScreenShredOn"
          (click)="isScreenShredOn = false; stopScreenSharing()"
          src="assets/images/meeting_menubar_icons/share_screen.png"
          height="50px" width="50px" alt srcset>
        <img class="bg-secondary rounded-2 p-1 cursor-pointer"
          *ngIf="!isScreenShredOn"
          (click)="isScreenShredOn = true; startScreenSharing()"
          src="assets/images/meeting_menubar_icons/cancel_sharing.png"
          height="50px" width="50px" alt srcset>
        <img class="bg-danger rounded-2 p-1 cursor-pointer"
          (click)="leaveMeeting()"
          src="assets/images/meeting_menubar_icons/end_meeting.png"
          height="50px" width="50px" alt srcset>
      </div>
    </div>
  </div>
  <div class="flex justify-center w-100">
    <div class="flex flex-row gap-2"
      style="min-width: 80dvw; max-width: 80dvw; min-height: 90dvh; max-height: 90dvh; overflow-x: auto; position: absolute; bottom: 0;">

      <div class="flex flex-row gap-1 w-100">
        <div class="card p-2 flex flex-column gap-2"
          style="min-width: 25%; max-width: 25%; min-height: 85dvh; max-height: 85dvh; overflow-x: auto; position: relative;">

          <strong>
            Candidate List
          </strong>
          <ng-container *ngFor="let user of userList">
            <div class="card p-3 flex flex-row mb-2 gap-2">
              <img class="mask mask-heart"
                src="assets/images/meeting/user_image.png"
                style="height: 50px !important; width: 50px !important;" />
              <div class="flex flex-col">
                <strong>{{user.FirstName}}</strong>
                <strong><small>{{user.LastName}}</small></strong>
              </div>
            </div>
          </ng-container>

        </div>
        <div class="card p-2"
          style="min-width: 74% !important; max-width: 74% !important; min-height: 85dvh; max-height: 85dvh; overflow-x: auto;">
          <ng-container *ngIf="false">
            <div class="flex flex-column gap-1">
              <div class="flex flex-row justify-content-between">
                <strong>
                  Objective
